    function handleFileSelect(evt) {
        evt.stopPropagation();
        evt.preventDefault();
        var files = [],
            items = evt.dataTransfer.items;

        function folderRead(entry){
            showFolder(entry);
            entry.createReader().readEntries(function (entries) {
                for(var i = 0; i < entries.length; i++){
                    var entry = entries[i];
                    console.log(entry);
                    if(entry.isFile){
                        entry.file(function(file){
                            show(file);
                        })
                    }else{
                        folderRead(entry);
                    }
                }
            });
        }


        for(var i = 0; i < items.length; i++){
            var entry = items[i].webkitGetAsEntry();
            if(!entry){
                return;
            }
            console.log(entry);
            if(entry.isFile){
                entry.file(function(file){
                    show(file);
                })
            }else{
                folderRead(entry);
            }
        }

        function show(f){
            var tpl = '<li><strong>'+ f.name + '</strong> || '
                + f.size +' bytes || last modified: ' +
                f.lastModifiedDate.toLocaleDateString() + '</li><li></li>';
            var lis = document.getElementsByTagName('li');
            lis[lis.length-1].innerHTML = tpl;
        }
        function showFolder(f){
            var tpl = '<li><strong>'+ f.name + '</strong></li><li></li>';
            var lis = document.getElementsByTagName('li');
            lis[lis.length-1].innerHTML = tpl;
        }
    }

